// Exports EdgeJS -> .NET Framework functionality to be used by other modules
var edge = require('edge');

// This is the assemblyLocation for the Vagrant test vm. Where will it really be? As is this is
// relative to the current working directory but can be constructured w/ __dirname if needed.
var assemblyLocation = 'dotnet_windows_api/dotnet_windows_api/bin/Release/dotnet_windows_api.dll';

exports.kill_process = edge.func({
  assemblyFile: assemblyLocation,
  typeName: 'dotnet_windows_api.EdgeBindings',
  methodName: 'KillAllProcessesByName'
});

