"use strict";

var fluid = require("universal");

var windows = fluid.registerNamespace("gpii.windows");

/**
 * Sets the DPI scale of the primary display.
 *
 * @param scale The target DPI scale, where 1 = 100% (of 96).
 * @return {{configured, maximum, actual}} The newly configured, actual, and maximum DPI scale.
 */
windows.display.setScreenDpi = function (scale) {

    // Return the new configuration.
    return windows.display.getScreenDpi();
};

/**
 * Get the configured, maximum, and actual DPI scale of the primary display.
 *
 * The maximum scale is the highest DPI scale that the current screen resolution supports. The actual scale is the
 * configured scale, capped at the maximum (until the resolution increases).
 *
 * @param {Object} [adapter]
 * @return {{configured, maximum, actual}} The configured, maximum, and actual DPI scale.
 */
windows.display.getScreenDpi = function (adapter) {
    fluid.warn("DPI not implemented");
    return {
        configured: 1,
        maximum: 1,
        actual: 1
    };
};
