"use strict";

var fluid = require("infusion");
var gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.windows.iod.chocolatey");

// Installs chocolatey packages
fluid.defaults("gpii.windows.iod.chocolateyInstaller", {
    gradeNames: ["fluid.component", "gpii.windows.iod.packageInstaller"],

    invokers: {
        installPackage: {
            funcName: "gpii.windows.iod.chocolatey.installPackage",
            args: ["{that}", "{serviceHandler}", "{arguments}.0"]
        },
        uninstallPackage: {
            funcName: "gpii.windows.iod.chocolatey.uninstallPackage",
            args: ["{that}", "{serviceHandler}", "{arguments}.0"]
        }
    }
});

/**
 * Install the package.
 *
 * @param {Component} that - The packageInstaller instance.
 * @param {Component} service - The service handler instance.
 * @return {Promise} Resolves when the action is complete.
 */
gpii.windows.iod.chocolatey.installPackage = function (that) {
    fluid.log("IoD.choco: Installing package " + that.installation.installerFile);

    var args = [ "install", "-y", that.installation.installerFile];
    return that.startElevatedProcess("choco", args);
};

/**
 * Uninstall the package.
 *
 * @param {Component} that The packageInstaller instance.
 * @param {Component} service The service handler instance.
 * @return {Promise} Resolves when the action is complete.
 */
gpii.windows.iod.chocolatey.uninstallPackage = function (that) {
    fluid.log("IoD.choco: Uninstalling package " + that.installation.installerFile);

    var args = [ "uninstall", "-y", that.installation.packageName];
    return that.startElevatedProcess("choco", args);
};
