"use strict";

var fluid = require("infusion");

var gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.windows.iod.exe");

// Additional packageData fields:
// desktop: Run it in the desktop (if elevate is true)

// Installs exe packages
fluid.defaults("gpii.windows.iod.exeInstaller", {
    gradeNames: ["fluid.component", "gpii.iod.windows.packageInstaller"],

    invokers: {
        installPackage: {
            funcName: "gpii.windows.iod.exe.install",
            args: ["{that}", "{that}.installation", true]
        },
        uninstallPackage: {
            funcName: "gpii.windows.iod.exe.install",
            args: ["{that}", "{that}.installation", false]
        }
    }
});

/**
 * Installs (or uninstalls) an EXE package.
 *
 * @param {Component} that The packageInstaller instance.
 * @param {Installation} installation The installation state.
 * @param {Boolean} install `true` to install the package, `false` to uninstall.
 * @return {Promise} Resolves when the action is complete.
 */
gpii.windows.iod.exe.install = function (that, installation, install) {
    var command;
    var args;
    if (install) {
        command = installation.installerFile;
        args = installation.packageData.installerArgs;
    } else {
        command = installation.uninstallCommand || installation.installerFile;
        args = installation.packageData.uninstallerArgs;
    }

    args = fluid.makeArray(args);
    fluid.log("IoD.exe: invoking exe " + command + " " + args.join(" "));

    return installation.packageData.elevate
        ? that.invokeElevated(command, args, {desktop: installation.packageData.desktop})
        : that.startProcess(command, args);
};
