"use strict";

var fluid = require("infusion");
var gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.windows.iod.language");

// Installs chocolatey packages
fluid.defaults("gpii.windows.iod.language", {
    gradeNames: ["fluid.component", "gpii.iod.packageInstaller"],

    invokers: {
        installPackage: {
            funcName: "gpii.windows.iod.language.installPackage",
            args: ["{that}"]
        },
        uninstallPackage: {
            funcName: "gpii.windows.iod.language.uninstallPackage",
            args: ["{that}"]
        }
    },

    packageTypes: "language"
});

/**
 * Install the language pack.
 *
 * @param {Component} that - The packageInstaller instance.
 * @return {Promise} Resolves when the action is complete.
 */
gpii.windows.iod.language.installPackage = function (that) {
    fluid.log("IoD.language: Installing package " + that.installerFile);
    return fluid.promise.resolve();
};

/**
 * Uninstall the language pack.
 *
 * @param {Component} that The packageInstaller instance.
 * @return {Promise} Resolves when the action is complete.
 */
gpii.windows.iod.language.uninstallPackage = function (that) {
    fluid.log("IoD.language: Uninstalling package " + that.installerFile);
    return fluid.promise.resolve();
};


