"use strict";

var fluid = require("infusion");

var child_process = require("child_process");

var gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.windows.iod.chocolatey");

// Installs chocolatey packages
fluid.defaults("gpii.windows.iod.chocolateyInstaller", {
    gradeNames: ["fluid.component", "gpii.iod.packageInstaller"],

    invokers: {
        prepareInstall: "fluid.identity",
        installPackage: {
            funcName: "gpii.windows.iod.chocolatey.installPackage",
            args: ["{that}", "{iod}", "{arguments}.0"]
        },
        cleanup: "fluid.identity",
        uninstallPackage: {
            funcName: "gpii.windows.iod.chocolatey.uninstallPackage",
            args: ["{that}", "{iod}", "{arguments}.0"]
        }
    },

    packageTypes: "chocolatey"
});


/**
 * Invokes chocolatey.
 *
 * @param installation {object} The package info (only used when resolving the promise).
 * @param args {string} An array of arguments to pass to the choco command.
 * @return {Promise} Resolves when complete.
 */
gpii.windows.iod.chocolatey.invoke = function (installation, args) {
    var promise = fluid.promise();

    fluid.log("IoD: Executing: choco " + args.join(" "));
    var child = child_process.spawn("choco", args);

    child.on("exit", function (code) {
        if (code) {
            promise.reject({
                isError: true,
                error: "Command returned exit code " + code
            });
        } else {
            promise.response(installation);
        }
    });

    child.stdout.on("data", function (data) {
        console.log(data.toString());
    });

    child.stderr.on("data", function (data) {
        console.log(data.toString());
    });

    return promise;
};

/**
 * Install the package.
 *
 * @param that {Component} The packageInstaller instance.
 * @param iod {Component} The gpii.iod instance.
 * @param installation {object} The installation state.
 * @return {Promise} Resolves to an object containing package information and installation state.
 */
gpii.windows.iod.chocolatey.installPackage = function (that, iod, installation) {
    fluid.log("IoD.choco: Installing package " + installation.filename);

    var args = [ "install", "-y", installation.localPackage];
    return gpii.windows.iod.chocolatey.invoke(installation, args);
};

/**
 * Uninstall the package.
 *
 * @param that {Component} The packageInstaller instance.
 * @param iod {Component} The gpii.iod instance.
 * @param installation {object} The installation state.
 * @return {Promise} Resolves to an object containing package information and installation state.
 */
gpii.windows.iod.chocolatey.uninstallPackage = function (that, iod, installation) {
    fluid.log("IoD.choco: Uninstalling package " + installation.filename);

    var args = [ "uninstall", "-y", installation.localPackage];
    return gpii.windows.iod.chocolatey.invoke(installation, args);
};


