"use strict";

var fluid = require("infusion");
require("../../WindowsUtilities/WindowsUtilities.js");
require("displaySettingsHandler");

var gpii = fluid.registerNamespace("gpii");
fluid.require("%universal");

fluid.defaults("gpii.platformReporter.windows", {
    gradeNames: ["gpii.platformReporter"],
    invokers: {
        getOSspecifics: "gpii.platformReporter.windows.getOSspecifics"
    }
});

gpii.platformReporter.windows.getOSspecifics = function () {
    debugger;
    return gpii.windows.display.getAllScreenResolutions();
};
