"use strict";

var fluid = require("gpii-universal");

require("./src/RegistryResolver.js");

fluid.defaults("gpii.windows.registryResolverConfig", {
    gradeNames: "fluid.component",
    resolvers: {
        registry: "gpii.windows.registryResolver"
    }
});

// Contribute the registration of the registryResolver into the LifecycleManager's configuration
fluid.makeGradeLinkage("gpii.windows.resolverConfigWithRegistry",
    ["gpii.lifecycleManager.standardResolverConfig"],
    "gpii.windows.registryResolverConfig"
);
