"use strict";

var fluid = require("universal");

var jqUnit = fluid.require("jqUnit");
var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.windows.tests.registryResolver");

require("../../../../index.js");

jqUnit.module("Registry Resolver");

jqUnit.test("Test Boolean Registry Lookups", function () {
    jqUnit.expect(2);

    jqUnit.assertDeepEq("Testing a registry key that always exists.",
        gpii.deviceReporter.registryKeyExists("HKEY_CURRENT_USER",
            "Software\\Microsoft\\ScreenMagnifier", "Magnification", "REG_DWORD"),
        true);

    jqUnit.assertDeepEq("Testing a registry key that does not exist.",
        gpii.deviceReporter.registryKeyExists("HKEY_CURRENT_USER",
            "Software\\Microsoft\\ScreenMagnifier", "NotARealSubPath", "REG_DWORD"),
        false);
});
