"use strict";

var fluid = require("gpii-universal"),
    TabletModeBindings = require("./src/build/Release/TabletMode");

var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.windows.tabletMode");

gpii.windows.tabletMode.setTabletMode = function (mode) {
    return TabletModeBindings.setTabletMode(mode);
};

gpii.windows.tabletMode.getTabletMode = function () {
    return TabletModeBindings.getTabletMode();
};

fluid.defaults("gpii.windows.tabletMode.setTabletMode", {
    gradeNames: "fluid.function",
    argumentMap: {
        "mode": 0
    }
});

fluid.defaults("gpii.windows.tabletMode.getTabletMode", {
    gradeNames: "fluid.function",
    argumentMap: {
    }
});
