"use strict";

var fluid = require("infusion");

var gpii = fluid.registerNamespace("gpii");
var Pcsclite = require("@pokusew/pcsclite");

fluid.registerNamespace("gpii.userListeners");

fluid.defaults("gpii.pcscUserListener.windows", {
    gradeNames: ["fluid.component"],
    invokers: {
        getPCSC: {
            funcName: "gpii.userListeners.getPCSC"
        }
    }
});

/**
 * Start the user listener.
 *
 * @param that {Component} The gpii.pcscUserListener instance.
 */
gpii.userListeners.getPCSC = function () {
    return gpii.userListeners.pcsc || (gpii.userListeners.pcsc = new Pcsclite());
};
