using System.Threading.Tasks;
using System.Security.Principal;

public class WindowsLogin
{
    /// <summary>
    /// Invoked by node, to get the current user's SID.
    /// </summary>
    /// <param name="input">Not used</param>
    /// <returns>The user's SID</returns>
    public async Task<object> GetUserSid(dynamic input)
    {
        return WindowsIdentity.GetCurrent().User.Value;
    }
}
