"use strict";

var fluid = require("infusion");

var jqUnit = fluid.require("node-jqunit");
var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.tests.userListener");

require("../index.js");

// Check that the card reader library gets loaded.
jqUnit.test("pcsclite test", function () {

    var userListeners = gpii.userListeners({
        listeners: {
            "onCreate.startListeners": "fluid.identity"
        },
        distributeOptions: {
            record: "gpii.userListeners.pcsc.windows",
            target: "{that pcsc}.options.gradeNames"
        }
    });

    var pcscLite = userListeners.pcsc.getPCSCLite();

    jqUnit.assertTrue("getPCSCLite should return something", !!pcscLite);
    jqUnit.assertEquals("getPCSCLite should return a PCSCLite instance", "[object PCSCLite]", pcscLite.toString());
});
