"use strict";

var fluid = require("gpii-universal");

var jqUnit = fluid.require("node-jqunit");
var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.tests.userListener");

require("../index.js");

jqUnit.module("gpii.tests.userListener.pcsc");

// Check that the card reader library gets loaded. The real tests are performed in universal.
jqUnit.test("pcsclite test", function () {

    var userListeners = gpii.userListeners({
        listeners: {
            "onCreate.startListeners": "fluid.identity"
        }
    });
    userListeners.pcsc.getPCSCLite();
    jqUnit.assert("getPCSCLite shouldn't crash");
});
