"use strict";

var fluid = require("universal"),
    fs = require("fs"),
    os = require("os"),
    readline = require("readline");

require("../../processHandling/processHandling.js");

var jqUnit = fluid.require("node-jqunit");
var gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.tests.metrics");

require("../index.js");

var teardowns = [];

jqUnit.module("gpii.tests.metrics", {
    teardown: function () {
        while (teardowns.length) {
            teardowns.pop()();
        }
    }
});

// Wrap the gpii.windowsMetrics to disable auto-starting the metrics capturing.
fluid.defaults("gpii.tests.metrics.windowsMetricsWrapper", {
    gradeNames: ["fluid.component", "gpii.windowsMetrics", "gpii.eventLog", "gpii.lifecycleManager"],
    listeners: {
        "onStartMetrics.application": null,
        "onStartMetrics.input": null,
        "onStopMetrics.application": null,
        "onStopMetrics.input": null
    }
});

gpii.tests.metrics.defaultKeyboardConfig = fluid.freezeRecursive();

// Tests for recordKeyTiming: key presses
gpii.tests.metrics.recordKeyTimingKeyTests = fluid.freezeRecursive({
    defaultState: {
        times: []
    },
    defaultConfig: {
        minSession: 0xffffff,
        sessionTimeout: 0xffffff,
        maxRecords: 1000
    },
    testData: [
        {   // Normal key press
            state: {},
            input: {
                timestamp: 1,
                key: undefined
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0}
            }
        },
        {   // special key
            state: null,
            input: {
                key: "BS"
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: "BS"}
            }
        },
        // Test that non-special keys don't get leaked.
        {   // not a special key (letter)
            state: null,
            input: {
                key: "A"
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE }
            }
        },
        {   // not a special key (symbol)
            state: null,
            input: {
                key: "!"
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE}
            }
        },
        {   // not a special key (numeric string)
            state: null,
            input: {
                key: "1"
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE}
            }
        },
        {   // not a special key (number)
            state: null,
            input: {
                key: 0x41
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE}
            }
        },
        {   // not a special key (string)
            state: null,
            input: {
                key: "ABC"
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE}
            }
        },
        {   // not a special key (virtual key code of a special key)
            state: null,
            input: {
                key: gpii.windows.API_constants.VK_ESCAPE
            },
            expect: {
                module: "metrics",
                event: "key-time",
                data: {keyTime: 0, key: fluid.NO_VALUE}
            }
        }
    ]
});

// Tests for recordKeyTiming: typing sessions
gpii.tests.metrics.typingSessionTests = fluid.freezeRecursive({
    defaultState: {
        times: []
    },
    defaultConfig: {
        minSession: 30000,
        sessionTimeout: 60000,
        minSessionKeys: 3
    },
    testData: [
        {   // Single session (3 keys, 1 minute)
            state: {},
            input: [
                { timestamp: 1 },
                { timestamp: 20000 },
                { timestamp: 60001 },
                { timestamp: 200000 }
            ],
            expect: [{
                module: "metrics",
                event: "typing-session",
                data: {
                    duration: 60000,
                    count: 3,
                    corrections: 0,
                    rate: 3
                }
            }]
        },
        {   // Single session (10 keys, 5 minutes)
            state: {},
            input: [
                { timestamp: 1 },
                { timestamp: 60000 },
                { timestamp: 90000 },
                { timestamp: 120000 },
                { timestamp: 150000 },
                { timestamp: 180000 },
                { timestamp: 210000 },
                { timestamp: 240000 },
                { timestamp: 270000 },
                { timestamp: 300001 },
                { timestamp: 9000000 }
            ],
            expect: [{
                module: "metrics",
                event: "typing-session",
                data: {
                    duration: 300000,
                    count: 10,
                    corrections: 0,
                    rate: 2
                }
            }]
        },
        {   // Two sessions
            state: {},
            input: [
                // 1st
                { timestamp: 1 },
                { timestamp: 20000 },
                { timestamp: 60001 },
                // 2nd
                { timestamp: 200000 },
                { timestamp: 250000 },
                { timestamp: 300000 },
                { timestamp: 350000 },
                { timestamp: 9000000 }
            ],
            expect: [{
                module: "metrics",
                event: "typing-session",
                data: {
                    duration: 60000,
                    count: 3,
                    corrections: 0,
                    rate: 3
                }
            }, {
                module: "metrics",
                event: "typing-session",
                data: {
                    duration: 150000,
                    count: 4,
                    corrections: 0,
                    rate: 2
                }
            }]
        }
    ]
});

